mapper.select:
select * from article_info
and add_user = #{userId}
order by add_time desc limit 0,5

select count(*)
from article_info article
left join user_article_message uam on article.id = uam.article_id
left join message_info message on message.id = uam.message_id


        uam.author_id = #{userId}


        and uam.article_id = #{articleId}

select article.id, article.title, article.subtitle, article.content, article.add_time, type.name as typeName
from article_info article
left join article_type type on article.type_id = type.id


        and article.add_user = #{userId}


order by article.add_time desc
limit #{limitEntity.offset},#{limitEntity.row}

select article.id, article.add_user, article.title, article.subtitle, article.content, article.add_time, type.name as typeName, user.name as authorName from article_info article left join article_type type on article.type_id = type.id left join user_info user on article.add_user = user.id
article.id = #{articleId}
group by article.id

select article.id, article.title, article.subtitle, article.content, article.add_time, type.name as typeName from article_info article left join article_type type on article.type_id = type.id
and article.add_user = #{authorId}
order by article.add_time desc

select count(*)
from article_info
where id = #{id}
