mapper.select:
select message.id, article.title, article.id as articleId, message.name, message.content, message.add_time as commentTime, message.email, message.web_site_url, message.status from message_info as message left join user_article_message as uam on message.id = uam.message_id left join article_info as article on uam.article_id = article.id
uam.author_id = #{userId}
order by message.add_time desc limit #{limitEntity.offset},#{limitEntity.row}

mapper.update:
update message_info


        status = #{status}


        modify_user = #{modifyUser}


where id=#{id}
